import React, { Component } from 'react';

export default class PurchaseForm extends Component {
    constructor(event){
        super(event);
        this.state={
            name:'',
            address:'',
            phonenumber:''
        }

    }
    handleSubmit=(event)=>{
        event.preventDefault();
        return(this.state)

    }

    handleChange=(event)=>{
        const {name,value}= event.target;
        this.setState({
            [name]:value
        })
        console.log(this.state);
    }



    render() {
        return (
            <div>
                <form>
                    <label htmlFor="name">Name</label>
                    <input type="text" name="name" placeholder="fullname" onChange={this.handleChange}/>
                    <label htmlFor="address">Address</label>
                    <input type="text" name="address" placeholder="address" onChange={this.handleChange}/>
                    <label htmlFor="phonenumber">Phone Number</label>
                    <input type="number" name="phonenumber" placeholder="fullname" onChange={this.handleChange}/>
                    <button type="submit" onSubmit={this.handleSubmit}>submit</button>
                </form>
            </div>
        )
    }
}
