import React, { Component } from 'react';

export default class PurchaseForm extends Component {
    constructor(event){
        super(event);
        this.state={
            name:'',
            address:'',
            phonenumber:'',
            isSubmitting:false
        }

    }
    handleSubmit=(event)=>{
        event.preventDefault();      
    }



    handleChange=(event)=>{
        const {name,value}= event.target;
        this.setState({
            [name]:value  
        },()=>{this.checkValue();})
    }
    checkValue=(event)=>{
        let Value=this.state;
        if (this.state.name.length>5 && this.state.address.length>5 && this.state.phonenumber.length>9){
            this.setState({
                isSubmitting:true
            })
        }
        else{
            this.setState({
                isSubmitting:false
            })
        }
    }
    /*this validation feedback is used for the checking whether the length of name is greater than 5 or not*/

    ValidationFeedback=()=>{
        let Feedback=this.state;
        if(this.state.name.length<5){
            return(<p>Fullname should be greater than five character</p>)
        }
    }




    render() {
        let btn=this.state.isSubmitting 
                ?<button type="submit">submit</button>
                :<button type="submit">Please fill the form to continue</button>
        return (
            <div>
                <form onSubmit={this.handleSubmit}>
                    <label htmlFor="name">Name</label>
                    <input type="text" name="name" placeholder="fullname" onChange={this.handleChange}/>
                    <Feedback/>
                    <label htmlFor="address">Address</label>
                    <input type="text" name="address" placeholder="address" onChange={this.handleChange}/>
                    <label htmlFor="phonenumber">Phone Number</label>
                    <input type="number" name="phonenumber" placeholder="fullname" onChange={this.handleChange}/>
                    {btn}
                </form>
            </div>
        )
    }
}
