import React, { Component } from 'react';

export default class PurchaseForm extends Component {
    constructor(event){
        super(event);
        this.state={
            name:'',
            address:'',
            phonenumber:'',
            isSubmitting:false,
            invalidField:true
        }

    }
    handleSubmit=(event)=>{
        event.preventDefault();      
    }



    handleChange=(event)=>{
        const {name,value}= event.target;
        this.setState({
            [name]:value  
        },()=>{this.checkValue();})
    }
    checkValue=(event)=>{
        let Value=this.state;
        if (this.state.name.length>5 && this.state.address.length>5 && this.state.phonenumber.length>9){
            this.setState({
                isSubmitting:true
            })
        }
        else{
            this.setState({
                isSubmitting:false
            })
        }
    }






    render() {
        let btn=this.state.isSubmitting 
                ?<button type="submit">submit</button>
                :<button type="submit" disabled>Please fill the form to continue</button>
        let checkFeedback=this.state.name.length>5
                ?<p>Correct</p>
                :<p>Name must be greater than 5 digits</p>        
        return (
            <div>
                <form className="was-validated" onSubmit={this.handleSubmit}>
                    <div className="form-group">
                    <label htmlFor="name">Name</label>
                    <input type="text" name="name" placeholder="fullname" onChange={this.handleChange} required/>
                    {checkFeedback}
                    </div>
                    <div className="form-group">
                    <label htmlFor="address">Address</label>
                    <input type="text" name="address" placeholder="address" onChange={this.handleChange}/>
                    <div className="valid-feedback">Valid.</div>
                    <div className="invalid-feedback">Please fill out this field.</div>
                    </div>
                    <div className="form-group">
                    <label htmlFor="phonenumber">Phone Number</label>
                    <input type="number" name="phonenumber" placeholder="fullname" onChange={this.handleChange}/>
                    <div className="valid-feedback">Valid.</div>
                    <div className="invalid-feedback">Please fill out this field.</div>                    
                    </div>

                    {btn}
                </form>
            </div>
        )
    }
}
