import React, { Component } from 'react';
import './admin.login.css';

export default class Admin extends Component {
    constructor(event){
        super();
        this.state={
            username:'',
            password:'',
            isSubmitting:false
        }
    }
    handleChange=(event)=>{
        const {name,value}=event.target;
        this.setState({
            [name]:value
        },()=>{this.checkError();})
        console.log(this.state);
    }

    checkError=(event)=>{
        let error=this.state;
        if(this.state.username.length>5 && this.state.password.length>8){
            this.setState({
                isSubmitting:true
            })
        }
        else{
            this.setState({
                isSubmitting:false
            })
        }
    }
    handleSubmit=(event)=>{
        event.preventDefault();
        console.log(this.state);
    }
    render() {
        let btn=this.state.isSubmitting
                    ?<button className="btn btn-success"><span>Login</span></button>
                    :<button className="button-container" disabled><span>Please provide username/password</span></button>
        return (
            <div className="contact">
                <div class="container register">
            <div class="row">
                <div class="col-md-12">
                    <ul class="nav nav-tabs nav-justified" id="myTab" role="tablist">
                        <li class="nav-item">
                            <a class="nav-link active" id="home-tab" data-toggle="tab" href="#home" role="tab" aria-controls="home" aria-selected="true">Login Form 1</a>
                        </li>
                        <li class="nav-item">
                            <a class="nav-link" id="profile-tab" data-toggle="tab" href="#profile" role="tab" aria-controls="profile" aria-selected="false">Login Form 2</a>
                        </li>
                    </ul>
                    <div class="tab-content" id="myTabContent">
                        <div class="tab-pane fade show active text-align form-new" id="home" role="tabpanel" aria-labelledby="home-tab">
                            <h3 class="register-heading">Login Form 1</h3>
                            <div class="row register-form">
                                <div class="col-md-12">
                                    <form method="post">
                                        <div class="form-group">
                                            <input type="text" name="LGform1_user" class="form-control" placeholder="Your Email *" value="" required=""/>
                                        </div>
                                        <div class="form-group">
                                            <input type="password" name="LGform1_pwd" class="form-control" placeholder="Your Password *" value="" required=""/>
                                        </div>
                                        <div class="form-group">
                                            <input type="submit" name="LGform1" class="btnContactSubmit" value="Login" />
                                            <a href="ForgetPassword.php" class="btnForgetPwd" value="Login">Forget Password?</a>
                                        </div>
                                    </form>
                                </div>
                            </div>
                        </div>

                    </div>
                </div>
            </div>
        </div>

<div className="col-md-6">
<h2>For any Query Please contact Us at:product@gmail.com</h2><br/>
                <p> Koteshwor,Kathmandu</p>
                <p>Contact:01-44588888,01-2344444</p>
                <p>Hotline Numbers:980233333,9841232333</p>
</div>
  </div>
        )
    }
}
