import React, { Component } from 'react';
import './admin.login.css';

export default class Admin extends Component {
    constructor(event){
        super();
        this.state={
            username:'',
            password:'',
            isSubmitting:false
        }
    }
    handleChange=(event)=>{
        const {name,value}=event.target;
        this.setState({
            [name]:value
        },()=>{this.checkError();})
        console.log(this.state);
    }

    checkError=(event)=>{
        let error=this.state;
        if(this.state.username.length>5 && this.state.password.length>8){
            this.setState({
                isSubmitting:true
            })
        }
        else{
            this.setState({
                isSubmitting:false
            })
        }
    }
    handleSubmit=(event)=>{
        event.preventDefault();
        console.log(this.state);
    }
    render() {
        let btn=this.state.isSubmitting
                    ?<button className="btn btn-success"><span>Login</span></button>
                    :<button className="button-container" disabled><span>Please provide username/password</span></button>
        return (
            <div className="contact">
        <div className="row">       
<div className="col-md-6">
<div className="container">
  <div className="card"></div>
  <div className="card">
      <h3 autoCapitalize="true">Admin Pannel</h3>
      <h5>Official Use Only</h5>
    <h1 className="title">Login</h1>
    <form onSubmit={this.handleSubmit}>
      <div className="input-container">
        <input type="name" id="#{label}" name="username" placeholder="username" onChange={this.handleChange}/>
        <label htmlFor="#{label}">Username</label>
        <div className="bar"></div>
      </div>
      <div className="input-container">
        <input type="password" id="#{label}" name="password" placeholder="password" onChange={this.handleChange}/>
        <label htmlFor="#{label}">Password</label>
        <div className="bar"></div>
      </div>
      {btn}
    </form>
  </div>
</div>

</div>
<div className="col-md-6">
<h2>For any Query Please contact Us at:product@gmail.com</h2><br/>
                <p> Koteshwor,Kathmandu</p>
                <p>Contact:01-44588888,01-2344444</p>
                <p>Hotline Numbers:980233333,9841232333</p>
</div>
</div>
  </div>
        )
    }
}
