           <ProductWrapper className="col-9 mx-auto col-md-6 col-lg-3 my-3">
               <div className="card">
                   <div className="img-container p-5" onClick={ this.handleClick }>
                    <Link to="/details">
                        <img src={img} alt="product" className="card-img-top"/>
                    </Link>

                   </div>
               </div>
           </ProductWrapper>
